INSERT INTO `sys_trans_def` VALUES ('1', '流程待办_执行人', 'return \"select a.ID_ id,b.SUBJECT name,a.ID_ 主键 ,b. SUBJECT 名称 from act_ru_task a,bpm_pro_run b where a.ASSIGNEE_={authorId} and b.ACTINSTID=a.PROC_INST_ID_\";', 'return \"update act_ru_task t set ASSIGNEE_ ={targetPersonId} where t.ID_ in ({ids})\";', '1', '1', 'admin', '2015-05-09 00:00:00', '{authorName} 把其为执行人的流程待办 {names} 转移给了 {targetPersonName};');
INSERT INTO `sys_trans_def` VALUES ('2', '代理配置_授权人', 'return \"select a.ID id,a.AGENT name,a.ID 主键,a.AGENT 代理人 from bpm_agent_setting a where a.AUTHID={authorId};\";', 'return \"update bpm_agent_setting a set a.AUTHID={targetPersonId} , a.AUTHNAME=\'{targetPersonName}\' where a.ID in({ids});\";', '1', '1', 'admin', '2015-05-09 00:00:00', '{authorName} 把他代理给 {names} 的 代理配置 转移给了 {targetPersonName}');
INSERT INTO `sys_trans_def` VALUES ('3', '代理配置_代理人', 'return \"select t.ID id,t.authName name, t.ID 主键,t.authName 授权人 from bpm_agent_setting t where t.AGENTID={authorId}\";', 'return \"update bpm_agent_setting t set t.AGENTID={targetPersonId},t.AGENT=\'{targetPersonName}\'  where t.ID in ({ids})\";', '1', '1', 'admin', '2015-05-09 00:00:00', '{authorName} 把授权人为 {names} 的代理给他的代理配置 转移给了 {targetPersonName}');
INSERT INTO `sys_trans_def` VALUES ('4', '角色转移', 'return \"select a.USERROLEID id,c.SYSNAME || \':\' ||b.roleName name,a.USERROLEID 主键,c.SYSNAME 系统名称,b.ROLENAME 角色名称 from SYS_USER_ROLE a,SYS_ROLE b left join sys_subsystem c on b.systemid=c.systemid where a.roleid=b.roleId    and  userId={authorId}\";', 'return \"update sys_user_role a set a.USERID=\'{targetPersonId}\' where a.USERROLEID in({ids})\";', '1', '1', 'admin', '2015-05-09 00:00:00', '{authorName} 把角色信息为 {names} 转移给了 {targetPersonName}');
INSERT INTO `sys_trans_def` VALUES ('5', '办结流程', 'return \"select distinct run.RUNID id,run.SUBJECT name,run.RUNID 主键,run.SUBJECT 名称 from BPM_PRO_RUN_HIS run where run.creatorid=\'{authorId}\' and (run.status = 2 or  run.status = 3) \";', 'return \"update bpm_pro_run_his run set run.CREATORID=\'{targetPersonId}\' ,run.CREATOR=\'{targetPersonName}\' where run.RUNID in ({ids})\";', '1', '1', 'admin', '2015-05-09 00:00:00', '{authorName} 把办结流程 {names} 转移给 {targetPersonName}');
INSERT INTO `sys_trans_def` VALUES ('6', '流程待办_候选人', 'return \"select ident.ID_ id,run.SUBJECT name, ident.ID_ 主键,run.SUBJECT 名称 from act_ru_task task,act_ru_identitylink ident,bpm_pro_run run where task.id_ = ident.TASK_ID_ and run.ACTINSTID=task.PROC_INST_ID_ and task.ASSIGNEE_=0 and ident.USER_ID_={authorId}\";', 'return \"update act_ru_identitylink ident set ident.USER_ID_={targetPersonId} where ident.ID_ in ({ids})\";', '1', '1', 'admin', '2015-05-09 00:00:00', '{authorName} 把其为候选人的流程待办 {names} 转移给了 {targetPersonName};');
INSERT INTO `sys_trans_def` VALUES ('7', '流程节点人员', 'return \"select a.NODEUSERID id,c.SUBJECT || \'-\' || d.NODENAME name,a.NODEUSERID 主键,c.SUBJECT 流程标题,d.NODENAME 节点名称 from bpm_node_user a,bpm_user_condition b,bpm_definition c,bpm_node_set d where a.CONDITIONID=b.ID and b.ACTDEFID=c.ACTDEFID and b.SETID=d.SETID and a.ASSIGNTYPE=\'users\' and  dbms_lob.instr(CMPIDS, \'{authorId}\', 1, 1)>0\";', 'import com.hotent.core.util.AppUtil;\r\nimport org.springframework.jdbc.core.JdbcTemplate;\r\n  \r\n  JdbcTemplate jdbcTemplate = AppUtil.getBean(JdbcTemplate.class);\r\n  String[] ids = \"{ids}\".split(\",\");\r\n  for(String id:ids){\r\n     String sql=\"select a.CMPIDS,a.CMPNAMES from bpm_node_user a where a.NODEUSERID=\"+id; \r\n     List<Map<String, Object>> mapList = jdbcTemplate.queryForList(sql);\r\n     Map map = mapList.get(0);\r\n     String cmpIds=\"\";\r\n     String cmpNames=\"\";\r\n     \r\n    \r\n    for(String cmpId:(map.get(\"CMPIDS\")+\"\").split(\",\")){\r\n        if(cmpId.equals(\"{authorId}\")){\r\n          cmpId=\"{targetPersonId}\";\r\n        }\r\n        if(cmpIds.length()>0){\r\n           cmpIds+=\",\"; \r\n         }\r\n        cmpIds+=cmpId;\r\n    }\r\n        \r\n    for(String cmpName:(map.get(\"CMPNAMES\")+\"\").split(\",\")){\r\n        if(cmpName.equals(\"{authorName}\")){\r\n          cmpName=\"{targetPersonName}\";\r\n        }\r\n        if(cmpNames.length()>0){\r\n         cmpNames+=\",\"; \r\n       }\r\n        cmpNames+=cmpName;\r\n    }\r\n     \r\n     sql=\"update bpm_node_user a set a.CMPIDS=\'\"+cmpIds+\"\' ,a.CMPNAMES=\'\"+cmpNames+\"\' where a.NODEUSERID=\"+id;    \r\n        \r\n     jdbcTemplate.execute(sql);\r\n   }\r\n  return \"\";', '1', '1', 'admin', '2015-05-09 00:00:00', '{authorName} 把流程节点设置 {names} 转移给了 {targetPersonName}');
