  `TYPEID` bigint(20) DEFAULT NULL COMMENT '分类ID',
  `SUBJECT` varchar(256) NOT NULL COMMENT '流程标题',
  `DEFKEY` varchar(128) NOT NULL COMMENT '流程定义Key\r\n            流程定义Key，由设计人员加上，唯一',
  `TASKNAMERULE` varchar(512) DEFAULT NULL COMMENT '任务标题生成规则',
  `DESCP` varchar(1024) DEFAULT NULL COMMENT '流程描述',
  `STATUS` smallint(6) DEFAULT NULL COMMENT '流程状态\r\n            0=草稿\r\n            1=发布\r\n            2=挂起\r\n            ',
  `DEFXML` text COMMENT '流程定义XML(设计器)',
  `ACTDEPLOYID` bigint(20) DEFAULT NULL COMMENT 'activiti流程定义ID',
  `ACTDEFKEY` varchar(255) DEFAULT NULL COMMENT 'act流程定义Key\r\n            act流程定义Key，其生成的规则为流程定义Key+动态生成值，保证其唯一，当流程定义删除时，本表记录会删除，但actiiti的流程定义的数据则不删除，这样发布的流程会跟原来的流程定义的key可能会同样，因此，后续再加动态值，保证其值会activit的流程定义中key也唯一.',
  `ACTDEFID` varchar(128) DEFAULT NULL,
  `CREATETIME` datetime DEFAULT NULL COMMENT '创建时间',
  `UPDATETIME` datetime DEFAULT NULL,
  `CREATEBY` bigint(20) DEFAULT NULL,
  `UPDATEBY` bigint(20) DEFAULT NULL,
  `REASON` varchar(512) DEFAULT NULL,
  `VERSIONNO` bigint(20) DEFAULT NULL,
  `PARENTDEFID` bigint(20) DEFAULT NULL COMMENT '隶属主定义ID\r\n            \r\n            当基于旧版本流程上进行新版本发布时，则生成一份新的记录，并且该记录的该字段则隶属于原来记录的主键id',
  `ISMAIN` smallint(6) DEFAULT NULL COMMENT '是否为主版本\r\n            1=主版本\r\n            0=非主版本\r\n            ',
  `TOFIRSTNODE` bigint(20) DEFAULT NULL COMMENT '跳转到第一个节点\r\n            0,不跳转\r\n            1,跳转',
  `SHOWFIRSTASSIGNEE` smallint(6) DEFAULT NULL COMMENT '是否可以选择执行人',
  `CANCHOICEPATH` varchar(500) DEFAULT NULL COMMENT '是否可以选择条件同步路径',
  `ISUSEOUTFORM` smallint(6) DEFAULT NULL,
  `FORMDETAILURL` varchar(200) DEFAULT NULL,
  `ALLOWFINISHEDCC` smallint(6) DEFAULT NULL COMMENT '是否允许流程结束时抄送',
  `SUBMITCONFIRM` smallint(6) DEFAULT NULL COMMENT '提交是否需要确认',
  `ALLOWDIVERT` smallint(6) DEFAULT NULL COMMENT '是否允许转办',
  `INFORMSTART` varchar(20) DEFAULT NULL COMMENT '归档时发送消息给发起人 类型',
  `INFORMTYPE` varchar(20) DEFAULT NULL COMMENT '通知类型',
  `ATTACHMENT` bigint(20) DEFAULT NULL COMMENT '帮助附件',
  `SAMEEXECUTORJUMP` smallint(6) DEFAULT NULL COMMENT '相同任务节点是否允许跳转',
  `ALLOWREFER` smallint(6) DEFAULT NULL COMMENT '允许参考数',
  `INSTANCEAMOUNT` smallint(6) DEFAULT NULL COMMENT '允许实例数量',
  `ALLOWFINISHEDDIVERT` smallint(6) DEFAULT NULL COMMENT '是否允许我的办结转发',
  `ISPRINTFORM` smallint(6) DEFAULT NULL COMMENT '是否开启打印模版功能',
  `DIRECTSTART` smallint(6) DEFAULT NULL COMMENT '直接启动不需要使用表单',
  `CCMESSAGETYPE` varchar(100) DEFAULT NULL COMMENT '抄送消息类型 ',
  `ALLOWDELDRAF` smallint(6) DEFAULT NULL,
  `TESTSTATUSTAG` varchar(100) DEFAULT NULL COMMENT '测试状态',
  `ALLOWMOBILE` int(11) DEFAULT '1' COMMENT '流程是否支持手机访问 0=不支持，1=支持',
  `SKIPSETTING` varchar(50) DEFAULT NULL COMMENT '跳过设置',
  PRIMARY KEY (`DEFID`),
  KEY `idx_bpmdef_actdefid` (`ACTDEFID`),
  KEY `idx_bpmdef_defkey` (`ACTDEFKEY`),
  KEY `IDX_BPMDEF_KEY` (`DEFKEY`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='流程定义扩展\r\nCode:DEFTN';

