INSERT INTO `bpm_form_template` VALUES ('10000000000001', '', 'main', 'fieldControl', '<script>\r\nfunction handRowEvent(ev,table){\r\n		$(\"td.tdNo\",table).each(function(i){\r\n			$(this).text(i+1);\r\n		});\r\n	}\r\n</script>\r\n<#setting number_format=\"0\">\r\n<#function getFieldList fieldList>\r\n 	<#assign rtn>\r\n		<#list fieldList as field>\r\n			<#if  field.isHidden == 0>\r\n				<tr>\r\n					<td align=\"right\" nowrap=\"nowarp\" style=\"width:20%;\" class=\"formTitle\" ><span>${field.fieldDesc}</span>:</td>\r\n					<td  class=\"formInput\" style=\"width:80%;\">\r\n						<@input field=field/>\r\n					</td>\r\n				</tr>\r\n			</#if>\r\n		</#list>\r\n 	</#assign>\r\n	<#return rtn>\r\n</#function>\r\n<#function setTeamField teams>\r\n 	<#assign rtn>\r\n		 <#list teams as team>\r\n				<tr>\r\n					<td colspan=\"2\" class=\"teamHead\">${team.teamName}</td>\r\n				</tr>\r\n				${getFieldList(team.teamFields)}\r\n		</#list>\r\n	</#assign>\r\n	<#return rtn>\r\n</#function>\r\n<br />\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"1\" class=\"formTable\">\r\n	<tr>\r\n		<td colspan=\"2\"  class=\"formHead\" >${table.tableDesc }</td>\r\n	</tr>\r\n	<#if teamFields??>\r\n		<#if isShow>\r\n			<#if showPosition == 1>\r\n				${setTeamField(teamFields)}\r\n				${getFieldList(fields)}\r\n			<#else>\r\n				${getFieldList(fields)}\r\n				${setTeamField(teamFields)}\r\n			</#if>\r\n		<#else>\r\n			${setTeamField(teamFields)}\r\n		</#if>\r\n	<#else>\r\n		${getFieldList(fields)}\r\n	</#if>\r\n</table>\r\n<br />\r\n', '', '0', 'oneColumn');
INSERT INTO `bpm_form_template` VALUES ('10000000000002', '', 'main', 'fieldControl', '<script>\r\nfunction handRowEvent(ev,table){\r\n		$(\"td.tdNo\",table).each(function(i){\r\n			$(this).text(i+1);\r\n		});\r\n	}\r\n</script><#setting number_format=\"0\">\r\n<#function getFieldList fieldList>\r\n 	<#assign rtn>\r\n 		<#assign index=0>\r\n		\r\n		<#list fieldList as field>\r\n			<#if field.isHidden == 0 && field.controlType!= 2 \r\n					&& field.controlType != 9 && field.controlType != 10 \r\n					&& field.controlType != 12 && field.controlType != 20 \r\n					&& field.controlType != 21 && field.controlType != 22>\r\n			<#if index % 2 == 0>\r\n			<tr>\r\n			</#if>\r\n				<td align=\"right\" style=\"width:15%;\" class=\"formTitle\" nowrap=\"nowarp\"><span>${field.fieldDesc}</span>:</td>\r\n				<td style=\"width:35%;\" class=\"formInput\">\r\n					<@input field=field/>\r\n				</td>\r\n				<#if index % 2 == 0 && !field_has_next>\r\n				<td style=\"width:15%;\" class=\"formTitle\"></td>\r\n				<td style=\"width:35%;\" class=\"formInput\"></td>\r\n				</#if>\r\n			<#if index % 2 == 1 || !field_has_next>\r\n			</tr>\r\n			</#if>\r\n			<#assign index=index+1>\r\n			</#if>\r\n		</#list>\r\n		\r\n		<#--(2)(9)ueditor(10)Office(12)(20)WebSign(21)(22)-->\r\n		<#list fieldList as field>\r\n			<#if field.controlType == 2 || field.controlType == 9 \r\n				|| field.controlType == 10 || field.controlType == 12 \r\n				|| field.controlType == 20 || field.controlType == 21\r\n				|| field.controlType == 22>\r\n				<tr>\r\n					<td align=\"right\" style=\"width:15%;\" class=\"formTitle\" nowrap=\"nowarp\"><span>${field.fieldDesc}</span></td>\r\n					<td style=\"width:35%;\" class=\"formInput\" colspan=\"3\">\r\n						<@input field=field/>\r\n					</td>\r\n				</tr>\r\n			</#if>\r\n		</#list>\r\n		\r\n 	</#assign>\r\n	<#return rtn>\r\n</#function>\r\n<#function setTeamField teams>\r\n 	<#assign rtn>\r\n		 <#list teams as team>\r\n				<tr>\r\n					<td colspan=\"4\" class=\"teamHead\">${team.teamName}</td>\r\n				</tr>\r\n				${getFieldList(team.teamFields)}\r\n		</#list>\r\n	</#assign>\r\n	<#return rtn>\r\n</#function>\r\n<br />\r\n<table cellpadding=\"2\" cellspacing=\"0\" border=\"1\" class=\"formTable\">\r\n	<tr>\r\n		<td colspan=\"4\"  class=\"formHead\" >${table.tableDesc }</td>\r\n	</tr>\r\n	<#if teamFields??>\r\n		<#if isShow>\r\n			<#if showPosition == 1>\r\n				${setTeamField(teamFields)}\r\n				${getFieldList(fields)}\r\n			<#else>\r\n				${getFieldList(fields)}\r\n				${setTeamField(teamFields)}\r\n			</#if>\r\n		<#else>\r\n			${setTeamField(teamFields)}\r\n		</#if>\r\n	<#else>\r\n		${getFieldList(fields)}\r\n	</#if>\r\n</table>\r\n<br />\r\n', '', '0', 'twoColumn');
