INSERT INTO `sys_index_column` VALUES ('1', '', 'userInfo', null, '0', '0', 'indexService.userInfo', null, null, null, null, '<div class=\"widget-box\">\r\n 	 <div class=\"widget-header\">\r\n			<h4 class=\"widget-title bigger lighter\"><i class=\"ace-icon fa fa-user\"></i>${model.title}</h4>\r\n  	</div>\r\n  <div class=\"widget-body\">\r\n    <div class=\"profile-user-info\">\r\n      <div class=\"profile-info-row\">\r\n        <div class=\"profile-info-name\"></div>\r\n        \r\n        <div class=\"profile-info-value\">\r\n          ${data.account}\r\n        </div>\r\n      </div>\r\n      <div class=\"profile-info-row\">\r\n        <div class=\"profile-info-name\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</div>\r\n        \r\n        <div class=\"profile-info-value\">\r\n          ${data.fullname}\r\n        </div>\r\n      </div>\r\n      <div class=\"profile-info-row\">\r\n        <div class=\"profile-info-name\"></div>\r\n        \r\n        <div class=\"profile-info-value\">\r\n      	<#if (data.org)??>${data.org.orgName}</#if>\r\n        </div>  \r\n      </div>\r\n      <div class=\"profile-info-row\">\r\n        <div class=\"profile-info-name\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</div>\r\n        \r\n        <div class=\"profile-info-value\">\r\n      	<#if (data.pos)??>${data.pos.posName}</#if>\r\n      </div>\r\n      </div>\r\n      <div class=\"profile-info-row\">\r\n        <div class=\"profile-info-name\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</div>\r\n        \r\n        <div class=\"profile-info-value\">\r\n          ${data.mobile}\r\n        </div>\r\n      </div>\r\n    </div>\r\n  </div>\r\n</div>\r\n', '1', null, '0', null, '0', null, null, '0');
INSERT INTO `sys_index_column` VALUES ('2', '', 'getMessage', null, '0', '0', 'indexService.getMessage', null, null, null, '/platform/system/messageReceiver/list.ht', '<div class=\"widget-box border \" >\n	<div class=\"widget-header\">\n		<h4 class=\"widget-title\"><i class=\"ht-icon fa fa-envelope-o\"></i>${model.title}</h4>\n		<div class=\"widget-toolbar\">\n			<a href=\"javascript:void(0);\" data-action=\"more\" data-url=\"${model.url}\" data-title=\"${model.title}\"  data-placement=\"bottom\" title=\"\">\n				<i class=\"ht-icon fa fa-list-alt\"></i>\n			</a>\n			<a href=\"javascript:void(0);\" data-action=\"reload\" data-placement=\"bottom\" title=\"\">\n				<i class=\"ht-icon fa fa-refresh\"></i>\n			</a>\n			<a href=\"javascript:void(0);\" data-action=\"collapse\"  data-placement=\"bottom\" title=\"\">\n				<i class=\"ht-icon fa fa-chevron-up\"></i>\n			</a>\n		</div>\n	</div>\n	<div class=\"widget-body\">\n		<div  class=\"widget-scroller\" data-height=\"${model.height}px\">\n			<ul class=\"widget-list list-unstyled\">\n					<#list data as data>\n               <li class=\"clearfix\" onclick=\"javascript:jQuery.openFullWindow(\'${ctx}/platform/system/messageRead/list.ht?messageId=${data.id}\')\">\n								${data.subject}\n						</li>\n\n				</#list>\n			</ul>\n		</div>\n	</div>\n</div>\n', '1', null, '0', null, '0', '', '', '0');
INSERT INTO `sys_index_column` VALUES ('3', '', 'pendingMatters', null, '0', '0', 'indexService.pendingMatters', null, null, null, '/platform/bpm/task/pendingMatters.ht', '<div class=\"widget-box border \" >\r\n	<div class=\"widget-header\">\r\n		<h4 class=\"widget-title\"><i class=\"ht-icon fa fa-bars\"></i>${model.title}</h4>\r\n		<div class=\"widget-toolbar\">\r\n			<a href=\"javascript:void(0);\" data-action=\"more\" data-url=\"${model.url}\" data-title=\"${model.title}\"  data-placement=\"bottom\" title=\"\">\r\n				<i class=\"ht-icon fa fa-list-alt\"></i>\r\n			</a>\r\n			<a href=\"javascript:void(0);\" data-action=\"reload\" data-placement=\"bottom\" title=\"\">\r\n				<i class=\"ht-icon fa fa-refresh\"></i>\r\n			</a>\r\n			<a href=\"javascript:void(0);\" data-action=\"collapse\"  data-placement=\"bottom\" title=\"\">\r\n				<i class=\"ht-icon fa fa-chevron-up\"></i>\r\n			</a>\r\n		</div>\r\n	</div>\r\n	<div class=\"widget-body\">\r\n		<div  class=\"widget-scroller\" data-height=\"${model.height}px\">\r\n			<#if data?exists> \r\n			<ul class=\"widget-list list-unstyled\"  >\r\n					<#list data as data>\r\n							<li class=\"clearfix\" onclick=\"javascript:jQuery.openFullWindow(\'${ctx}/platform/bpm/task/toStart.ht?taskId=${data.id}\')\">\r\n								<div class=\"pull-left\">\r\n									<p>\r\n										</p><h5><strong> ${data.subject}</strong></h5>\r\n								<p></p>\r\n                                  <p><i class=\"fa fa-clock-o\"></i> <abbr class=\"timeago\" title=\"\">${data.createTime?string(\"yyyy-MM-dd HH:mm:ss\")}</abbr></p>\r\n																								\r\n							</div>\r\n							<div class=\"text-right pull-right\">\r\n								<h4 class=\"cost\"> ${data.creator}</h4>\r\n								<p>\r\n									<span class=\"label label-success arrow-in-right\"><i class=\"fa fa-check\"></i> </span>\r\n								</p>\r\n							</div>\r\n						</li>\r\n				</#list>\r\n			</ul>\r\n			<#else>\r\n				<div class=\"alert alert-info\"></div>\r\n			</#if>\r\n		</div>\r\n	</div>