mapper.select:
SELECT FROM webag_baginfo
order by ${orderField} ${orderSeq}
order by ID desc

SELECT * FROM ( SELECT , C.ID_ TASK_ID,C.TASK_DEF_KEY_,B.RUNID FLOW_RUNID,C.PROC_INST_ID_, C.NAME_ NODE_NAME_,C.CREATE_TIME_ TASK_CREATE_TIME_ FROM webag_baginfo A,BPM_PRO_RUN B , ACT_RU_TASK C WHERE A.ID=B.BUSINESSKEY_NUM AND B.ACTINSTID=C.PROC_INST_ID_ AND C.ASSIGNEE_=#{userId}
union
SELECT , C.ID_ TASK_ID,C.TASK_DEF_KEY_,B.RUNID FLOW_RUNID,C.PROC_INST_ID_, C.NAME_ NODE_NAME_,C.CREATE_TIME_ TASK_CREATE_TIME_ FROM webag_baginfo A,BPM_PRO_RUN B , ACT_RU_TASK C, ACT_RU_IDENTITYLINK D WHERE A.ID=B.BUSINESSKEY_NUM AND B.ACTINSTID=C.PROC_INST_ID_ AND C.id_ = D.TASK_ID_ AND D.USER_ID_ = #{userId} AND C.ASSIGNEE_ =0 and C.DESCRIPTION_!='39'
UNION
SELECT , C.ID_ TASK_ID,C.TASK_DEF_KEY_,B.RUNID FLOW_RUNID,C.PROC_INST_ID_, C.NAME_ NODE_NAME_,C.CREATE_TIME_ TASK_CREATE_TIME_ FROM webag_baginfo A,BPM_PRO_RUN B , ACT_RU_TASK C, ACT_RU_IDENTITYLINK D WHERE A.ID=B.BUSINESSKEY_NUM AND B.ACTINSTID=C.PROC_INST_ID_ AND C.id_ = D.TASK_ID_ AND C.ASSIGNEE_ =0 and C.DESCRIPTION_!='39' AND D.type_ = 'org' and D.group_id_ in (select uo.orgid from SYS_USER_POS uo where uo.userid= #{userId} )
UNION
SELECT , C.ID_ TASK_ID,C.TASK_DEF_KEY_,B.RUNID FLOW_RUNID,C.PROC_INST_ID_, C.NAME_ NODE_NAME_,C.CREATE_TIME_ TASK_CREATE_TIME_ FROM webag_baginfo A,BPM_PRO_RUN B , ACT_RU_TASK C, ACT_RU_IDENTITYLINK D WHERE A.ID=B.BUSINESSKEY_NUM AND B.ACTINSTID=C.PROC_INST_ID_ AND C.id_ = D.TASK_ID_ AND C.ASSIGNEE_ =0 and C.DESCRIPTION_!='39' AND D.type_ = 'role' and D.group_id_ in (select ur.roleid from sys_user_role ur where ur.userid=#{userId} )
union
SELECT , C.ID_ TASK_ID,C.TASK_DEF_KEY_,B.RUNID FLOW_RUNID,C.PROC_INST_ID_, C.NAME_ NODE_NAME_,C.CREATE_TIME_ TASK_CREATE_TIME_ FROM webag_baginfo A,BPM_PRO_RUN B , ACT_RU_TASK C, ACT_RU_IDENTITYLINK D WHERE A.ID=B.BUSINESSKEY_NUM AND B.ACTINSTID=C.PROC_INST_ID_ AND C.id_ = D.TASK_ID_ AND C.ASSIGNEE_ =0 and C.DESCRIPTION_!='39' AND D.type_ = 'pos' and D.group_id_ in (select up.posid from sys_user_pos up where up.userid= #{userId} )
) tmp
AND upper(tmp.subject) LIKE #{subject} AND upper(tmp.processName) LIKE #{processName} AND tmp.creatorId =#{creatorId} and tmp.TASK_CREATE_TIME_>=#{beginCreateTime,jdbcType=TIMESTAMP} and tmp.TASK_CREATE_TIME_ <=#{endCreateTime,jdbcType=TIMESTAMP}
AND tmp.description_ = #{type}
order by tmp.TASK_CREATE_TIME_ desc
order by ${orderField} ${orderSeq}
